var exports, http;

http = require('http');

exports = function(code) {
  return http.get("http://hq.sinajs.cn/list=" + code, function(res) {
    res.on('data', function(data) {
      var content;
      console.log(data.toString());
      content = data.toString().split("=")[1];
      return console.log(content);
    });
    return console.log("Got response: " + res.statusCode);
  }).on('error', function(e) {
    return console.log("Got error: " + e.message);
  });
};

exports("sh600167");
